azureLogAnalytics.query:
let min_length_8 = (s: string) {
    let len = strlen(s);
    case(len == 1, strcat(s, s, s, s, s, s, s, s),
        len == 2 or len == 3, strcat(s, s, s, s),
        len == 4 or len == 5 or len == 6 or len == 7, strcat(s, s),
        s)
};
let ai_hash = (s: string) {
    abs(toint(__hash_djb2(min_length_8(s))))
};

requests
// additional filters can be applied here
| where $__timeFilter(timestamp)
| where "All" in ($operation_name) or operation_Name in ($operation_name)
| where "All" in ($result_code) or resultCode in ($result_code)
| where success == 'False'
| project Name = operation_Name, ["Response Code"] = resultCode, ["Response Time"] = duration, OperationID = operation_Id, Score=ai_hash(operation_Id)
| top 20 by Score asc
